void SysTick_Init(void){
    NVIC_ST_CTRL_R = 0;
    NVIC_ST_CURRENT_R = 0;
    NVIC_ST_RELOAD_R = 600000;
    NVIC_SYS_PRI3_R = (NVIC_SYS_PRI3_R & 0x00FFFFFF) | 0x40000000; // priority 2
    NVIC_ST_CTRL_R = 0x06; // core clock and interrupts
}

void Song_Init(void){
    SysTick_Init();
		PortF_Init();
}

void Song_Play(void){
	note = 0;
    counter = dur[note];
    Sound_Play(pitch[note]);
    NVIC_ST_CTRL_R |= 0x01;
}

void Song_Stop(void){
    uint8_t i;
    NVIC_ST_CTRL_R &= ~0x01;
    Sound_Play(0);
}

void SysTick_Handler(void){
    if (--counter) {
        return;
    }
		if(++note == sizeof(dur)){
				Song_Stop();
				toggle_blue();
				return;
		}
		Sound_Play(pitch[note]);
    counter = dur[note];
		toggle_red();
}
